import HotelIcon from '@mui/icons-material/Hotel';

const HouseDescription = () => {

    const house_id = window.location.href.split('/')[4];
    const url = 'http://127.0.0.1:5000/api/${house_id}'
const [mounted,setMounted] = useState(true)
  const [house,setHouse] =useState("")
useEffect(() => {
const loadData=async()   =>{
    const {data} =await axios.get(url);

    if(mounted){
        setHouse(data);
    }
};
 loadData();
   return ()=> [
       setMounted(false)
   ]
    
    }, [mounted]);


    return (
        <div className='HouseDescription__container'>
{house && <div className='HouseDescription__leftContainer'>
    <img src={house.house_details.house_image} alt="" />     
    
    </div>  }
    
    <div className='HouseDescription__leftContainer'>
    
       </div>

       </div>
    )
}

export default HouseDescription;
