import axios from 'axios';
import React,{useEffect,useState} from 'react'
import { useDispatch, useSelector } from "react-redux";
import { houseForSaleAction } from '../store/Actions/HouseFetchAction';
import "../StyleSheet/HouseList.css";

const ForSale = () => {

  const url = "/api/house-sale";
  const [houses,setHouses] = useState([]);
  useEffect(() => {
    axios
    .get(url)
    .then((response) => {
      setHouses(response.data);
      console.log(response.data); 
    })
    .catch((error) => {
      console.log(error);
    });
  },[]);


    return (
        <div className='Houses__container'>
          {
            houses && <div className='HouseList__container'>

              {
                houses.map((house) => {
                  return (
            




<div className="House__details" key={house._id}>
<div className="img-conatiner">
                   
                   <img src={house.house_details.house_image} alt="House Picture" />

               </div>
<div className="House__priceAndDetails">
  <h4 className="House__price">{house.house_details.price}</h4>
  <h4 className="House__snipDescription">'${house.house_details.numOfBedRooms} Bedroom house for sale in $ {house.house_location.province}'</h4>
</div>
</div>
                  )
                })
              }
              </div>
          }
        </div>
    )
}

export default ForSale
